parameters.jsCode:
console.log('Full input:', JSON.stringify($input.all(), null, 2));
const inputData = $input.first().json;
console.log('Input data:', JSON.stringify(inputData, null, 2));

// Look for metadata in different possible locations
console.log('Checking for metadata...');
if (inputData.metadata) {
  console.log('Found metadata:', JSON.stringify(inputData.metadata, null, 2));
}

// Sanitize chatInput - handle different input formats and escape special characters
let sanitizedMessage = '';
if (inputData.chatInput) {
  // Remove any control characters and sanitize
  sanitizedMessage = inputData.chatInput
    .toString()
    .replace(/[\u0000-\u001F\u007F-\u009F]/g, '') // Remove control characters
    .replace(/"/g, '\\"')                          // Escape double quotes
    .trim();
} else if (inputData.message) {
  // Handle alternate message field
  sanitizedMessage = inputData.message
    .toString()
    .replace(/[\u0000-\u001F\u007F-\u009F]/g, '') 
    .replace(/"/g, '\\"')
    .trim();
}

console.log('Sanitized message:', sanitizedMessage);

return [{
  json: {
    message: sanitizedMessage,
    session_id: inputData.sessionId,
    action: inputData.action,
    user_auth: inputData.metadata || null,
    debug: 'Checking for metadata'
  }
}];

